mapper.select:
	SELECT * FROM
	(
		SELECT
        	A.*,
        	ROWNUM AS RNUM,
        	COUNT(*) OVER() AS TOTCNT
        FROM(
        	 SELECT *  FROM PHOTO WHERE M_ID=#{M_ID}
        	 	AND PHOTO_DELETE_DATE IS NULL
            ORDER BY PHOTO_NO DESC
        ) A
	)WHERE RNUM BETWEEN #{STARTRN} AND #{ENDRN}
