mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, A.* FROM( SELECT * FROM REVIEW WHERE 1 = 0
OR REV_TITLE LIKE '%${REV_TITLE}%'
OR REV_CONTENT LIKE '%${REV_CONTENT}%'
OR M_ID LIKE '%${M_ID}%'
OR REV_DESTINATION LIKE '%${REV_DASTINATION}%'
OR 1=1
AND REV_NO = #{REV_NO}
AND REV_CREATE_DATE between #{startdate} and #{enddate}
AND REV_DELETE_DATE IS NULL order by REV_NO DESC) A ) WHERE RN BETWEEN ${STARTRN} AND ${ENDRN}

mapper.update:
UPDATE REVIEW_RECOMMEND SET
REV_RCM=#{REV_RCM},
REV_RCM_UPDATE_DATE=#{REV_RCM_UPDATE_DATE,jdbcType=DATE}
WHERE REV_RCM_NO=#{REV_RCM_NO}

mapper.select:
		SELECT * FROM REVIEW_RECOMMEND

		WHERE
		
			
				REV_RCM_NO=#{REV_RCM_NO,jdbcType=INTEGER}
			
			
				M_ID=#{M_ID,jdbcType=VARCHAR} AND REV_NO= #{REV_NO,jdbcType=INTEGER}
